INSERT INTO `sup_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2014-10-20 23:22:13', '2014-10-20 23:22:13', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2014-10-20 23:22:13', '2014-10-20 23:22:13', '', 0, 'http://pixelcureinteractive.com/supple/?p=1', 0, 'post', '', 1),
(2, 1, '2014-10-20 23:22:13', '2014-10-20 23:22:13', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\r\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like piña coladas. (And gettin'' caught in the rain.)</blockquote>\r\n...or something like this:\r\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\r\nAs a new WordPress user, you should go to <a href="http://pixelcureinteractive.com/supple/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'About Supple', '', 'publish', 'open', 'open', '', 'about-supple', '', '', '2014-10-22 01:28:26', '2014-10-22 01:28:26', '', 0, 'http://pixelcureinteractive.com/supple/?page_id=2', 0, 'page', '', 0),
